import { Component } from 'react';
import ContactForm from './ContactForm';
import ContactList from './ContactList';
import Filter from './Filter';

export class App extends Component {
  state = {
    contacts: [
      { id: 'id-1', name: 'Rosie Simpson', number: '459-12-56' },
      { id: 'id-2', name: 'Hermione Kline', number: '443-89-12' },
      { id: 'id-3', name: 'Eden Clements', number: '645-17-79' },
      { id: 'id-4', name: 'Annie Copeland', number: '227-91-26' },
    ],
    filter: '',
  };

  contactId = nanoid();

  handleSubmitFormData = data => {
    console.log(data);
    // if (this.state.contacts.filter(contact => contact.name === data.name)) {
    //   alert('1234567890-98765432123456789');
    // }
    this.setState(prevState => {
      return {
        contacts: [
          ...prevState.contacts,
          {
            id: this.contactId(),
            name: data.name,
            number: data.number,
          },
        ],
      };
    });
  };

  handleFilterData = data => {
    this.setState({
      filter: data,
    });
  };

  handleDeleteContact = contactId => {
    this.setState(prevState => ({
      contacts: prevState.contacts.filter(contact => contact.id !== contactId),
    }));
  };

  render() {
    return (
      <div>
        <h1>Phonebook</h1>
        <ContactForm onSubmit={this.handleSubmitFormData} />

        <h2>Contacts</h2>
        <Filter onSubmit={this.handleFilterData} />
        <ContactList
          contacts={this.state.contacts}
          filter={this.state.filter}
          onDeleteContact={this.handleDeleteContact}
        />
      </div>
    );
  }
}
